    //-------------------------------------------------------------------------------------
    &#39;patternType&#39; =&gt; &#39;classic&#39;,

    //-------------------------------------------------------------------------------------
    // Change Uri
    //-------------------------------------------------------------------------------------
    //
    // URI adreslerine rota vermek için kullanılır.
    //
    // Kullanım: @key -&gt; yeni adres, @value -&gt; eski adres
    //
    // array
    // (
    //     &#39;anasayfa&#39;     =&gt; &#39;home/index&#39;
    // );
    //
    //-------------------------------------------------------------------------------------
    &#39;changeUri&#39; =&gt;
    [
        &#39;simplicity&#39; =&gt; &#39;Simplicity&#39;,
        &#39;home&#39;       =&gt; &#39;Simplicity&#39;
    ]
],

//-------------------------------------------------------------------------------------
// URI
//-------------------------------------------------------------------------------------
&#39;uri&#39; =&gt;
[
    //-------------------------------------------------------------------------------------
    // Lang
    //-------------------------------------------------------------------------------------
    //
